import React, { useEffect, useState } from "react";
import { useGlobalContext } from "../context/AppContext";

const NewsSection = () => {
  const { apiUrl } = useGlobalContext();
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Esegui la chiamata all'API del New York Times
        const response = await fetch(apiUrl);
        const data = await response.json();

        // Aggiorna lo stato con i dati ottenuti
        setNews(data.results);
      } catch (error) {
        console.error("Errore durante il recupero delle notizie:", error);
      }
    };

    fetchData();
  }, [apiUrl]); // Ora l'effetto dipende da apiUrl

  return (
    <div className="news-section">
      {news.map((article) => (
        // Renderizza gli articoli qui
        // Assicurati di visualizzare le informazioni desiderate
        <div key={article.id}>
          <h2>{article.title}</h2>
          <p>{article.abstract}</p>
          {article.multimedia && article.multimedia[0] && (
            <img
              src={article.multimedia[0].url}
              alt={article.multimedia[0].caption}
            />
          )}
          <p>{article.body}</p>
          {/* ... altri dettagli dell'articolo */}
        </div>
      ))}
    </div>
  );
};

export default NewsSection;
