// NewsSection.js
import React, { useEffect, useState } from 'react';
import LoadingSpinner from './LoadingSpinner';
import '../assets/css/newsSection.css';

const NewsSection = ({ section }) => {
  const [loading, setLoading] = useState(true);
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Mostra il loader prima di iniziare il fetch
        setLoading(true);

        const response = await fetch(
          `https://api.nytimes.com/svc/topstories/v2/${section.toLowerCase()}.json?api-key=tGSXuRhPkNnojNA6gvTNHTClun0h8w0K`
        );
        const data = await response.json();
        setNews(data.results);
      } catch (error) {
        console.error('Errore durante il recupero delle notizie:', error);
      } finally {
        // Nascondi il loader una volta completato il fetch
        setLoading(false);
      }
    };

    fetchData();
  }, [section]);

  return (
    <div className="news-section">
      {loading ? (
        // Se è in corso il fetch, mostra il loader
        <LoadingSpinner />
      ) : (
        // Altrimenti, mostra le notizie
        news &&
        news.map((article) => (
          <div key={article.id} className="article-container">
            {/* Contenuto dell'articolo */}
          </div>
        ))
      )}
    </div>
  );
};

export default NewsSection;
