// Importa il tuo componente LoadingSpinner
import LoadingSpinner from './LoadingSpinner';
import React, { useEffect, useState } from 'react';
import { useGlobalContext } from '../context/AppContext';
import '../assets/css/newsSection.css';

const NewsSection = ({ section }) => {
  const { formatSection } = useGlobalContext();
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Mostra il loader prima di iniziare il fetch
        setLoading(true);

        const response = await fetch(`https://api.nytimes.com/svc/topstories/v2/${section.toLowerCase()}.json?api-key=tGSXuRhPkNnojNA6gvTNHTClun0h8w0K`);
        const data = await response.json();
        setNews(data.results);
      } catch (error) {
        console.error('Errore durante il recupero delle notizie:', error);
      } finally {
        // Nascondi il loader una volta completato il fetch
        setLoading(false);
      }
    };

    fetchData();
  }, [section]);

  return (
    <div className="news-section">
      {loading ? (
        // Se è in corso il fetch, mostra il loader
        <LoadingSpinner />
      ) : (
        // Altrimenti, mostra le notizie
        news &&
        news.map((article, index) => (
          <div key={index} className="article-container">
            <div>
              <h2 className="article-title">{article.title}</h2>
              <p className="article-abstract">{article.abstract}</p>
            </div>
            {article.multimedia && article.multimedia.length > 0 && (
              <img
                key={article.multimedia[0].url}
                src={article.multimedia[0].url}
                alt={article.multimedia[0].caption}
                className="article-image"
              />
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default NewsSection;
