// Navbar.js
import React, { useState } from 'react';
import moment from 'moment';
import 'moment/locale/it';
import Nav from './Nav';
import HamburgerMenu from './HamburgerMenu';
import SearchBar from './SearchBar';
import '../assets/css/navbar.css';

const Navbar = () => {
  const currentDate = moment().locale('en').format('dddd, D MMMM YYYY');
  const [currentSection, setCurrentSection] = useState('Home');
  const [searchQuery, setSearchQuery] = useState('');

  const handleSetCurrentSection = (section) => {
    setCurrentSection(section);
  };

  const handleSearch = () => {
    // Puoi implementare la logica di ricerca qui
    console.log('Ricerca:', searchQuery);
    // Aggiungi la logica per gestire i risultati della ricerca, ad esempio aggiornando lo stato globale con i risultati
  };

  const handleSearchInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchInputKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <nav className="navbar">
      <div className='top-section'>
        <div>
          <div className='left-section'>
            <div className='hamburger'>
              <HamburgerMenu
                sections={['Home', 'World', 'U.S.', 'Politics', 'N.Y.', 'Business', 'Art', 'Opinion', 'Health', 'Real Estate', 'Technology', 'Books']}
                setCurrentSection={handleSetCurrentSection}
              />
            </div>
            <SearchBar
              searchQuery={searchQuery}
              onSearch={handleSearch}
              onInputChange={handleSearchInputChange}
              onInputKeyPress={handleSearchInputKeyPress}
            />
          </div>
        </div>
        <div className="logo">
          <img
            src={require('../assets/images/thenewyorktimeslogo.png')}
            alt="New York Times Logo"
          />
        </div>
        <div className='icon-account'>
          <img
            src={require('../assets/images/icon-account.png')}
            alt="Icon Account"
          />
        </div>
      </div>
      <div className="bottom-section">
        <div className="current-date">
          {currentDate}
        </div>
        <hr className="divider" />
        <Nav setCurrentSection={handleSetCurrentSection} />
        <hr className="divider" />
      </div>
    </nav>
  );
};

export default Navbar;
