// App.js
import React, { useState } from 'react';
import Navbar from './components/Navbar';
import NewsSection from './components/NewSection';
import NewsSearch from './components/NewsSearch';
import { AppProvider } from './context/AppContext';

function App() {
  const [currentSection, setCurrentSection] = useState('Home');
  const [searchResults, setSearchResults] = useState([]);

  // Funzione di callback per ricevere i risultati della ricerca
  const handleSearchResults = (results) => {
    setSearchResults(results);
  };

  return (
    <AppProvider>
      <div>
        <Navbar />
        <NewsSearch onSearchResults={handleSearchResults} />
        {/* Passa i risultati della ricerca alla NewsSection */}
        <NewsSection section={currentSection} searchResults={searchResults} />
      </div>
    </AppProvider>
  );
}

export default App;
