// Section.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const API_KEY = 'tGSXuRhPkNnojNA6gvTNHTClun0h8w0K';

const Section = () => {
  const { sectionId } = useParams();
  const [sectionData, setSectionData] = useState([]);

  useEffect(() => {
    fetch(`https://api.nytimes.com/svc/topstories/v2/${sectionId}.json?api-key=${API_KEY}`)
      .then(response => response.json())
      .then(data => setSectionData(data.results))
      .catch(error => console.error(`Errore durante la richiesta dei contenuti per la sezione ${sectionId}:`, error));
  }, [sectionId]);

  return (
    <div>
      <h2>{sectionId.toUpperCase()}</h2>
      <ul>
        {sectionData.map(item => (
          <li key={item.url}>
            <a href={item.url} target="_blank" rel="noopener noreferrer">{item.title}</a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Section;
