import React, { useEffect, useState } from 'react';
import { useGlobalContext } from '../context/AppContext';
import '../assets/css/newSection.css';

// ... Altre importazioni ...

const NewSection = ({ section }) => {
  const { formatSection } = useGlobalContext();
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`https://api.nytimes.com/svc/topstories/v2/${section.toLowerCase()}.json?api-key=tGSXuRhPkNnojNA6gvTNHTClun0h8w0K`);
        const data = await response.json();
        setNews(data.results);
      } catch (error) {
        console.error('Errore durante il recupero delle notizie:', error);
      }
    };

    fetchData();
  }, [section]);

  return (
    <div className="news-section">
      {news.map((article, index) => {
        const { id, title, abstract, multimedia } = article;

        return (
          <div key={id} className="article-container">
            <div>
              <h2 className="article-title">{title}</h2>
              <p className="article-abstract">{abstract}</p>
            </div>
            {multimedia && multimedia.length > 0 && (
              <img
                key={`${multimedia[0].url}-${index}`}
                src={multimedia[0].url}
                alt={multimedia[0].caption || 'No caption'}
                className="article-image"
              />
            )}
          </div>
        );
      })}
    </div>
  );
};

export default NewSection;
