import React, { useState, useEffect } from 'react';

const API_KEY = 'LA_TUA_CHIAVE_API'; // Sostituisci con la tua chiave API

function Nav() {
  const [sections, setSections] = useState([]);

  useEffect(() => {
    // Effettua una richiesta per ottenere le sezioni disponibili
    fetch(`https://api.nytimes.com/svc/news/v3/content/section-list.json?api-key=${API_KEY}`)
      .then(response => response.json())
      .then(data => {
        // Estrai solo i nomi delle sezioni
        const sectionNames = data.results.map(section => section.display_name);
        setSections(sectionNames);
      })
      .catch(error => console.error('Errore durante la richiesta delle sezioni:', error));
  }, []);

  const handleSectionClick = (section) => {
    // Qui puoi gestire la navigazione o il caricamento dei contenuti per la sezione cliccata
    console.log(`Hai cliccato su: ${section}`);
  };

  return (
    <div>
      <ul className="nav justify-content-center custom-nav">
        {sections.map((section) => (
          <li className="nav-item" key={section}>
            <button
              className="nav-link"
              onClick={() => handleSectionClick(section)}
            >
              {section}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Nav;
