import React, { createContext, useContext } from "react";

const AppContext = createContext();

const AppProvider = ({ children }) => {
  const apiKey = 'tGSXuRhPkNnojNA6gvTNHTClun0h8w0K';
  const numOfResults = 5;  
  const apiUrl = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=${apiKey}&show-fields=thumbnail,body&top=true&num_of_results=${numOfResults}`;

  const sections = [
    "home",
    "world",
    "us",
    // ... altre sezioni ...
  ];

  const formatSection = (section) => {
    switch (section) {
      case "us":
        return "U.S.";
      case "nyregion":
        return "N.Y";
      case "t-magazine":
        return "magazine";
      case "realestate":
        return "real estate";
      default:
        return section;
    }
  };

  return (
    <AppContext.Provider value={{ sections, formatSection, apiUrl }}>
      {children}
    </AppContext.Provider>
  );
};

const useGlobalContext = () => {
  return useContext(AppContext);
};

export { AppProvider, useGlobalContext };
