// Nav.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const API_KEY = 'LA_TUA_CHIAVE_API';

const Nav = () => {
  const [sections, setSections] = useState([]);

  useEffect(() => {
    fetch(`https://api.nytimes.com/svc/news/v3/content/section-list.json?api-key=${API_KEY}`)
      .then(response => response.json())
      .then(data => {
        const sectionNames = data.results.map(section => section.display_name);
        setSections(sectionNames);
      })
      .catch(error => console.error('Errore durante la richiesta delle sezioni:', error));
  }, []);

  return (
    <div>
      <ul className="nav justify-content-center custom-nav">
        {sections.map(section => (
          <li className="nav-item" key={section}>
            <Link to={`/section/${section.toLowerCase()}`} className="nav-link">
              {section}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Nav;
