// Nel tuo AppContext.js
const AppProvider = ({ children }) => {
    const apiKey = 'tuo-api-key';
    const apiUrl = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=${apiKey}&show-fields=thumbnail,body`;

    const sections = [
      "home",
      "world",
      "us",
      // ... altre sezioni ...
    ];

    const formatSection = (section) => {
      switch (section) {
        case "us":
          return "U.S.";
        case "nyregion":
          return "N.Y";
        case "t-magazine":
          return "magazine";
        case "realestate":
          return "real estate";
        default:
          return section;
      }
    };

    return (
      <AppContext.Provider value={{ sections, formatSection, apiUrl }}>
        {children}
      </AppContext.Provider>
    );
  };
