// SearchBar.js
import React from 'react';

const SearchBar = ({ searchQuery, onInputChange, onSearch, onInputKeyPress, isVisible, toggleVisibility }) => {
  return (
    <div className="search-container">
      <div onClick={toggleVisibility} className={`search-icon ${isVisible ? 'visible' : ''}`}>
        <img
          src={require('../assets/images/icon-search.png')}
          alt="Icona di ricerca"
          style={{ width: '24px', height: '24px' }}
        />
      </div>

      {isVisible && (
        <div className="search-input-container">
          <input
            type="text"
            placeholder="SEARCH"
            style={{ width: '200px' }}
            value={searchQuery}
            onChange={onInputChange}
            onKeyPress={onInputKeyPress}
          />
        </div>
      )}
    </div>
  );
};

export default SearchBar;
