import React, { useState } from 'react';
import '../assets/css/SearchBar.css'; // Assicurati di avere un file CSS collegato con il nome giusto

// ...

const SearchBar = () => {
    const [isSearchVisible, setIsSearchVisible] = useState(false);

    const toggleSearchVisibility = () => {
      setIsSearchVisible(!isSearchVisible);
    };

    return (
      <div className="search-container">
        <div onClick={toggleSearchVisibility} className="search-icon">
          {/* Icona della lente di ricerca con colore nero */}
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
            <path fill="#000" d="M15.5 14h-.79l-.28-.27C16.41 11.59 18 9.1 18 6 18 2.69 15.31 0 12 0S6 2.69 6 6s2.69 6 6 6c1.1 0 2.11-.38 2.91-1.01l.27.28v.79l5 4.99L20.49 14l-4.99-5zm-3.5 0C9.01 14 6 10.99 6 7.5S9.01 1 12 1s6 3.01 6 6.5-2.99 6.5-6 6.5z" />
          </svg>
        </div>

        {isSearchVisible && (
          <div className="search-input-container">
            {/* Barra di ricerca */}
            <input
              type="text"
              placeholder="Cerca..."
              // Altri attributi o stili per la barra di ricerca
            />
            {/* Altri contenuti o azioni relative alla ricerca */}
          </div>
        )}
      </div>
    );
  };

  // ...


export default SearchBar;
