// NewsSearch.js
import React, { useState } from 'react';

const NewsSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearch = async () => {
    // Effettua la chiamata API al New York Times per cercare notizie
    try {
      setIsLoading(true);
      const apiKey = 'tGSXuRhPkNnojNA6gvTNHTClun0h8w0K'; // Sostituisci con la tua chiave API del New York Times
      const response = await fetch(`https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${searchQuery}&api-key=${apiKey}`);
      const data = await response.json();

      // Estrai gli articoli dalla risposta
      const articles = data.response.docs.map((doc) => ({
        headline: doc.headline.main,
        snippet: doc.snippet,
        source: doc.source,
        // Aggiungi altri campi desiderati
      }));

      setSearchResults(articles);
    } catch (error) {
      console.error('Errore durante la ricerca delle notizie:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Inserisci la tua ricerca"
        value={searchQuery}
        onChange={handleInputChange}
      />
      <button onClick={handleSearch} disabled={isLoading}>
        Cerca
      </button>

      {isLoading && <p>Caricamento...</p>}

      {searchResults.length > 0 && (
        <div>
          <h2>Risultati della ricerca:</h2>
          <ul>
            {searchResults.map((article, index) => (
              <li key={index}>
                <h3>{article.headline}</h3>
                <p>{article.snippet}</p>
                <p>Fonte: {article.source}</p>
                {/* Aggiungi altri campi o stili secondo necessit√† */}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default NewsSearch;
