// NewsSearch.js
import React, { useState } from 'react';

const NewsSearch = ({ onSearchResults }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearch = async () => {
    try {
      setIsLoading(true);
      const apiKey = 'YOUR_NYT_API_KEY'; // Sostituisci con la tua chiave API del New York Times
      const response = await fetch(`https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${searchQuery}&api-key=${apiKey}`);
      const data = await response.json();

      const articles = data.response.docs.map((doc) => ({
        headline: doc.headline.main,
        snippet: doc.snippet,
        source: doc.source,
        // Aggiungi altri campi desiderati
      }));

      // Chiamiamo la funzione di callback passando i risultati della ricerca
      onSearchResults(articles);
    } catch (error) {
      console.error('Errore durante la ricerca delle notizie:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Inserisci la tua ricerca"
        value={searchQuery}
        onChange={handleInputChange}
      />
      <button onClick={handleSearch} disabled={isLoading}>
        Cerca
      </button>

      {isLoading && <p>Caricamento...</p>}
    </div>
  );
};

export default NewsSearch;
