import React, { useEffect, useState } from "react";
import { useGlobalContext } from "../context/AppContext";

const NewsSection = () => {
  const { sections, formatSection } = useGlobalContext();
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=tGSXuRhPkNnojNA6gvTNHTClun0h8w0K`
        );
        const data = await response.json();
        setNews(data.results);
      } catch (error) {
        console.error("Errore durante il recupero delle notizie:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="news-section">
      {news &&
        news.map((article) => (
          <div key={article.id}>
            <h2>{article.title}</h2>
            <p>{article.abstract}</p>
            {article.multimedia && article.multimedia.length > 0 && (
              <img
                src={article.multimedia[0].url} // Puoi usare l'URL dell'immagine desiderata
                alt={article.multimedia[0].caption}
              />
            )}
            {/* ... altri dettagli dell'articolo */}
          </div>
        ))}
    </div>
  );
};

export default NewsSection;
