import React, { useState } from 'react';
import '../assets/css/SearchBar.css'; // Assicurati di avere un file CSS collegato con il nome giusto

// ...

const SearchBar = () => {
    const [isSearchVisible, setIsSearchVisible] = useState(false);

    const toggleSearchVisibility = () => {
      setIsSearchVisible(!isSearchVisible);
    };

    return (
      <div className="search-container">
        <div onClick={toggleSearchVisibility} className="search-icon">
          <img
            src={require('../assets/images/icon-search.png')}
            alt="Icona di ricerca"
            style={{ width: '24px', height: '24px' }}
          />
        </div>

        {isSearchVisible && (
          <div className="search-input-container">
            <input
              type="text"
              placeholder="Cerca..."
              style={{ width: '200px'}}
            />
          </div>
        )}
      </div>
    );
  };

  // ...




export default SearchBar;
