import React, { useEffect, useState } from 'react';
import '../assets/css/nav.css';

function Nav() {
  const [sections, setSections] = useState([]);

  useEffect(() => {
    const fetchSections = async () => {
      try {
        const apiKey = 'tGSXuRhPkNnojNA6gvTNHTClun0h8w0K'; // Sostituisci con la tua chiave API
        const response = await fetch(
          `https://api.nytimes.com/svc/topstories/v2/sections.json?api-key=${apiKey}`
        );
        const data = await response.json();
        setSections(data.results);
      } catch (error) {
        console.error('Errore durante il recupero delle sezioni:', error);
      }
    };

    fetchSections();
  }, []);

  return (
    <div>
      {sections.map((section) => (
        <div key={section.section}>
          <h3>{section.title}</h3>
          <ul className="nav justify-content-center custom-nav">
            {section.subsections.map((subsection) => (
              <li className="nav-item" key={subsection.section}>
                <a className="nav-link" href={subsection.url}>
                  {subsection.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default Nav;
