import React, { useEffect, useState } from "react";
import { useGlobalContext } from "../context/AppContext";

const NewsSection = () => {
  const { sections, formatSection } = useGlobalContext();
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Esegui la chiamata all'API del New York Times
        const response = await fetch(
          `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=tGSXuRhPkNnojNA6gvTNHTClun0h8w0K`
        );
        const data = await response.json();

        // Aggiorna lo stato con i dati ottenuti
        setNews(data.results);
      } catch (error) {
        console.error("Errore durante il recupero delle notizie:", error);
      }
    };

    fetchData();
  }, []); // Dipendenza vuota per assicurarsi che l'effetto si verifichi solo al montaggio

  return (
    <div className="news-section">
      {news.map((article) => (
        // Renderizza gli articoli qui
        // Assicurati di visualizzare le informazioni desiderate
        <div key={article.id}>
          <h2>{article.title}</h2>
          <p>{article.abstract}</p>
          {/* ... altri dettagli dell'articolo */}
        </div>
      ))}
    </div>
  );
};

export default NewsSection;
