// HamburgerMenu.js 
import React, { useState } from 'react';
import '../assets/css/hamburgermenu.css';

const HamburgerMenu = ({ sections, setCurrentSection }) => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  const handleClick = (section) => {
    setCurrentSection(section);
    setMenuOpen(false); // Chiudi il menu dopo il clic
    window.location.hash = `#${section}`; // Cambia l'hash dell'URL
  };

  return (
    <div className="hamburger-menu">
      <div className={`hamburger-icon ${isMenuOpen ? 'open' : ''}`} onClick={toggleMenu}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      {isMenuOpen && (
        <div className="menu">
          {sections.map((section) => (
            <a key={section} href={`#${section}`} onClick={() => handleClick(section)}>
              {section}
            </a>
          ))}
        </div>
      )}
    </div>
  );
};

export default HamburgerMenu;
