import React from 'react';
import moment from 'moment';
import 'moment/locale/it';
import { Navbar as BootstrapNavbar, Button, Collapse } from 'react-bootstrap';
import '../assets/css/navbar.css';  // Assicurati che il percorso sia corretto

import Nav from './Nav';

const Navbar = () => {
  const currentDate = moment().locale('en').format('dddd, D MMMM YYYY');

  return (
    <BootstrapNavbar expand="lg" variant="dark">
      <div className="container-fluid">
        {/* Contenuto del menu a sinistra */}
        <Collapse in={false}>
          <div className="bg-dark p-4" id="navbarToggleExternalContent">
            <h5 className="text-body-emphasis h4">Collapsed content</h5>
            <span className="text-body-secondary">Toggleable via the navbar brand.</span>
          </div>
        </Collapse>

        {/* Hamburger Menu Button */}
        <Button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarToggleExternalContent"
          aria-controls="navbarToggleExternalContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </Button>

        {/* Logo al centro */}
        <div className="logo">
          <img
            src={require('../assets/images/thenewyorktimeslogo.png').default}  // Modifica per gestire correttamente le immagini
            alt="New York Times Logo"
          />
        </div>
      </div>

      {/* Sezione inferiore con data e linee divisorie */}
      <div className="bottom-section">
        <div className="current-date">
          {currentDate}
        </div>
        <hr className="divider" />
        <Nav />
        <hr className="divider" />
      </div>
    </BootstrapNavbar>
  );
};

export default Navbar;
